"use strict";

var modal = document.querySelector('#icon-block-modal');
var modalContent = document.querySelector('#modal-content');

function activateOffCanvasNav() {
  var hamburger = document.querySelector('#hamburger-menu');

  hamburger.classList.toggle('active');
}

function activateModal(e) {
  var iconBlockContent = e.currentTarget.parentNode.cloneNode(true);

  modal.style.display = 'block';
  modalContent.appendChild(iconBlockContent);
}

function closeModal() {
  modal.style.display = 'none';
  modalContent.querySelectorAll('*').forEach(child => child.remove());
}

// INITIALIZERS

document.addEventListener('DOMContentLoaded', function() {
   document.querySelector('#hamburger-menu').addEventListener('click', activateOffCanvasNav);
   document.querySelectorAll('.icon-block-button').forEach(el => {el.addEventListener('click', activateModal)});
   document.querySelector('.close-button-img').addEventListener('click', closeModal);
});
